import React from 'react';
import s from './MyPosts.module.css'
import Post from './Post/Post';

const MyPosts = (props) => {

    let newPostElement = React.createElement();

    let addPost = () => {
        let text = newPostElement.current.value;
        //alert(text);
    };

    return (
        <div>
            <div>
            <textarea ref={newPostElement} value='привет'></textarea>
                <div><button onClick={ addPost }>Создать новый пост </button></div>
            </div>
            {props.posts.map((el)=> {return <Post post={el.post} id={el.id}/>})}
        </div>
    );
};

export default MyPosts;
