import React from 'react';
import s from './Dialogs.module.css'
import DialogItem from "./DialogItem/DialogsItem";
import Message from "./Message/Message";




const Dialogs = (props) => {

    let addMassage = React.createRef();

    let addNewMassage = () => {
        let text = addMassage.current.value;
        return(
            <DialogItem name='Саша' id='5'/>
            
        )

    };

    return (
        <div className={`${s.dialogs} ${s.grid}`}>
            <div className={s.dialogsItem}>
                {props.data.map(el => {return <DialogItem name={el.name} id={el.id}/>
                })}
            </div>
            <div className={s.massages}>
                {props.message.map(el => { return <Message message={el.message}/>})}
            </div>
            <textarea ref={addMassage}></textarea>
            <button onClick={addNewMassage}>Добавить новое сообщение></button>
        </div>
    )
};


export default Dialogs;
